
public class Percentage {
	
	public static void main(String args[]) {
	// TODO Auto-generated method stub
	char grade = 'f';
	switch (grade) {
		case 'A':
		System.out.println("grade A percentage below 85-100");
		break;
		case 'B':
		System.out.println("grade B percentage below 65-100");
		break;
		case 'C':
		System.out.println("grade c percentage below 45-100 ");
		break;
		case 'D':
		System.out.println("grade D percentage below 35");
		break;
		default:
		System.out.println("grade E ");
		}
	
	}
}
